import * as React from "react";
import {
  Formik,
  FormikHelpers,
  FormikProps,
  Form,
  Field,
  FieldProps,
} from "formik";
import * as yup from "yup";

// interface MyFormValues {
//   firstName: string;
//   lastName: string;
//   dateOfBirth: Date;
//   email: string;
//   mobileNumber: string;
//   dateOfGraduation: Date;
//   employed: false;
//   employer: string;
// }

const myFormValues = yup.object({
  firstName: yup.string(),
  lastName: yup.string(),
  dateOfBirth: yup.date(),
  email: yup.string(),
  mobileNumber: yup.string(),
  dateOfGraduation: yup.date(),
  employed: yup.boolean(),
  employer: yup.string(),
});

type Person = yup.InferType<typeof myFormValues>;

export const AddForm = () => {
  const initialValues: Person = {
    firstName: "Insert first name",
    lastName: "Insert last name",
    dateOfBirth: new Date("1995-12-17T03:24:00"),
    email: "Insert e-mail",
    mobileNumber: "Insert phone as 07538758805",
    dateOfGraduation: new Date("1995-12-17T03:24:00"),
    employed: false,
    employer: "Insert employer",
  };

  return (
    <div>
      <h1>My Example</h1>
      <Formik
        initialValues={initialValues}
        onSubmit={(values, actions) => {
          console.log({ values, actions });
          alert(JSON.stringify(values, null, 2));
          actions.setSubmitting(false);
        }}
      >
        <Form>
          <label htmlFor="firstName">First Name</label>
          <Field id="firstName" name="firstName" placeholder="First Name" />

          <label htmlFor="dateOfGraduation">Graduation date</label>
          <Field
            id="dateOfGraduation"
            name="dateOfGraduation"
            placeholder="dateOfGraduation"
            type="date"
          />

          <label htmlFor="employed">Employed</label>
          <Field id="employed" name="employed" type="checkbox" />

          <label htmlFor="employer">Employer</label>
          <Field as="select" id="employer" name="employer">
            <option value="designer">Designer</option>
            <option value="development">Developer</option>
            <option value="product">Product Manager</option>
            <option value="other">Other</option>
          </Field>

          <button type="submit">Submit</button>
        </Form>
      </Formik>
    </div>
  );
};

export default AddForm;
