import * as React from "react";
import {
  Formik,
  FormikHelpers,
  FormikProps,
  Form,
  Field,
  FieldProps,
} from "formik";
import * as yup from "yup";

// interface MyFormValues {
//   firstName: string;
//   lastName: string;
//   dateOfBirth: Date;
//   email: string;
//   mobileNumber: string;
//   dateOfGraduation: Date;
//   employed: false;
//   employer: string;
// }

const personSchema = yup.object({
  firstName: yup
    .string()
    .max(15, "Must be 15 characters or less")
    .required("First name required"),
  lastName: yup
    .string()
    .max(20, "Must be 20 characters or less")
    .required("Last name required"),
  dateOfBirth: yup
    .date()
    .typeError("The value must be a date (YYYY-MM-DD)")
    .required("Date of birth required"),
  email: yup
    .string()
    .email("Invalid email address")
    .required("E-mail required"),
  mobileNumber: yup
    .string()
    .typeError("That doesn't look like a mobile number")
    // .positive("That doesn't look like a mobile number")
    // .integer("A phone number can't include a decimal point")
    .required("A mobile number required"),
  dateOfGraduation: yup
    .date()
    .typeError("The value must be a date (YYYY-MM-DD)")
    .required("Date of graduation required"),
  employed: yup.boolean(),
  employer: yup.string().max(20, "Must be 20 characters or less"),
  role: yup
    .string()
    .oneOf(["designer", "development", "product", "other"], "Invalid Job Role"),
});

// const MyCheckbox = ({ children, ...props }) => {
//   // React treats radios and checkbox inputs differently other input types, select, and textarea.
//   // Formik does this too! When you specify `type` to useField(), it will
//   // return the correct bag of props for you -- a `checked` prop will be included
//   // in `field` alongside `name`, `value`, `onChange`, and `onBlur`
//   const [field, meta] = useField({ ...props, type: "checkbox" });

//   return (
//     <div>
//       <label className="checkbox-input">
//         <input type="checkbox" {...field} {...props} />
//         {children}
//       </label>
//       {meta.touched && meta.error ? (
//         <div className="error">{meta.error}</div>
//       ) : null}
//     </div>
//   );
// };

type Person = yup.InferType<typeof personSchema>;

export const AddForm = () => {
  const initialValues: Person = {
    firstName: "Insert first name",
    lastName: "Insert last name",
    dateOfBirth: new Date("1995-12-17T03:24:00"),
    email: "Insert e-mail",
    mobileNumber: "Insert phone as 07538758805",
    dateOfGraduation: new Date("1995-12-17T03:24:00"),
    employed: false,
    employer: "Insert employer",
    role: "Insert role",
  };

  return (
    <div>
      <h1>My Example</h1>
      <Formik
        initialValues={initialValues}
        onSubmit={(values, actions) => {
          console.log({ values, actions });
          alert(JSON.stringify(values, null, 2));
          actions.setSubmitting(false);
        }}
      >
        <Form>
          <label htmlFor="firstName">First Name</label>
          <Field id="firstName" name="firstName" placeholder="First Name" />

          <label htmlFor="dateOfGraduation">Graduation date</label>
          <Field
            id="dateOfGraduation"
            name="dateOfGraduation"
            placeholder="dateOfGraduation"
            type="date"
          />

          <label htmlFor="employed">Employed</label>
          <Field id="employed" name="employed" type="checkbox" />

          <label htmlFor="employer">Employer</label>
          <Field id="employer" name="employer" placeholder="_nology" />

          <label htmlFor="role">Role</label>
          <Field as="select" id="role" name="role">
            <option value="designer">Designer</option>
            <option value="development">Developer</option>
            <option value="product">Product Manager</option>
            <option value="other">Other</option>
          </Field>

          <button type="submit">Submit</button>
        </Form>
      </Formik>
    </div>
  );
};

export default AddForm;
